export function CrimePodcastSection() {
  const [activeTab, setActiveTab] = useState("all");
  const [playingPodcast, setPlayingPodcast] = useState(null); // تحديد البودكاست الذي يتم تشغيله

  const filteredPodcasts =
    activeTab === "all"
      ? podcasts
      : podcasts.filter((podcast) => podcast.category === activeTab);

  return (
    <section className="w-full bg-white py-12 text-black">
      <div className="container mx-auto px-4 md:px-6">
        <div className="flex flex-col items-start gap-4">
          <div className="space-y-2">
            <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl">
              <span className="text-[#61090b]">CRIME</span>CAST
            </h2>
            <p className="max-w-[700px] text-gray-700 md:text-xl">
              Dive into the world of true crime, cold cases, and forensic investigations.
            </p>
          </div>

          <div className="flex space-x-4">
            {["all", "true-crime", "cold-cases", "heists"].map((tab) => (
              <button
                key={tab}
                className={`px-4 py-2 rounded ${
                  activeTab === tab ? "bg-[#61090b] text-white" : "bg-gray-200"
                }`}
                onClick={() => setActiveTab(tab)}
              >
                {tab.replace("-", " ").toUpperCase()}
              </button>
            ))}
          </div>

          <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3 mt-6">
            {filteredPodcasts.map((podcast) => (
              <PodcastCard
                key={podcast.id}
                podcast={podcast}
                playingPodcast={playingPodcast}
                setPlayingPodcast={setPlayingPodcast}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

function PodcastCard({ podcast, playingPodcast, setPlayingPodcast }) {
  const isPlaying = playingPodcast === podcast.id; // هل يتم تشغيل هذا البودكاست؟

  return (
    <div className="overflow-hidden bg-zinc-900 text-white rounded-lg shadow-lg">
      <div className="relative">
        {!isPlaying ? (
          <>
            <img
              src={podcast.image || "/placeholder.svg"}
              alt={podcast.title}
              className="aspect-[16/9] w-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent" />
            <button
              className="absolute bottom-4 right-4 h-12 w-12 rounded-full border-2 border-white bg-black/50 text-white hover:bg-[#61090b]"
              onClick={() => setPlayingPodcast(podcast.id)}
            >
              <Play className="h-6 w-6" />
              <span className="sr-only">Play episode</span>
            </button>
          </>
        ) : (
          <div className="w-full aspect-[16/9]">
            <iframe
              className="w-full h-full"
              src={podcast.video.replace("watch?v=", "embed/")}
              title={podcast.title}
              allow="autoplay; encrypted-media"
              allowFullScreen
            ></iframe>
          </div>
        )}
      </div>
      <div className="p-4">
        <h3 className="line-clamp-1 text-lg font-bold">{podcast.title}</h3>
        <p className="text-zinc-400">{podcast.date} • {podcast.duration}</p>
      </div>
      <div className="p-4 pt-0">
        <p className="line-clamp-2 text-sm text-zinc-300">{podcast.description}</p>
      </div>
      <div className="border-t border-zinc-800 p-4">
        <button
          className="ml-auto px-0 text-red-500 hover:text-red-400"
          onClick={() => setPlayingPodcast(isPlaying ? null : podcast.id)}
        >
          {isPlaying ? "Stop" : "Listen Now"}
        </button>
      </div>
    </div>
  );
}


